  const fn = props.init()
  const track_type = props.type || "any"
  if (!has("watch")(props) || !has("func")(props)) return null
  const [diff, setDiff] = useRecoilState(props.tracker.diff)
  const [prev, setPrev] = useRecoilState(props.tracker.prev)
