    const diffDiff = difference(props.watch || [])(diffSum)
    if ((track_type === "any" && diffSum.length !== 0) || isEmpty(diffDiff)) {
      setDiff([])
      fn[props.name]()
    } else {
      setDiff(diffSum)
    }
    setPrev(cur)
  }, arr)
  return null
